<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class OffersM extends MY_Model {    protected $_table = 'offers';    protected $_primary_key = "id";    protected $return_type = "array";    protected $before_create = array('prep_date');     public function prep_date($users) {        $users['created_at'] = date('Y-m-d');        return $users;    }        public function count_jobs($id){        return $this->count_by('id',$id);            }}